import './Projects.sass';
import SectionHeading from '../../Components/SectionHeading/SectionHeading.js';


function ShowProjects() {
  const projects = [
    {
      name:'aavarkhatiwoda.com',
      description:'Personal SWE Portfolio Website',
      technologies:'React · JSX · Sass',
      content:[
        '- Self-taught [3+ years] and developed a minimalistic and scalable SWE portfolio website',
        '- Utilized Sass variables and mapping of key-value objects and lists toward creating a scalable\
        and readable environment',
        '- Implemented for mobile and desktop, up to 4k',
        '- Emphasized design toward a high-quality user interface and user experience',
        '- Tested and transferred improvements and additions from a testing environment to the main directory',
      ],
    },
    {
      name:'Apple Music Terminal Music Finder',
      description:'',
      technologies:'Python · Apple Music [Mac]',
      link:'https://github.com/aavarkhatiwoda/python/blob/main/musicFinder.py',
      content:[
        '- Created a terminal program that numerically lists potential songs through\
        matching of text in string input and plays the intended song from download\
        artists library after selection',
        '- Incorporated handling of special characters in the input',
      ],
    },
    {
      name:'Recursive Sudoku Solver Visualizer',
      description:'',
      technologies:'Python · PyGame',
      link:'https://github.com/aavarkhatiwoda/python/blob/main/sudokuSolver.py',
      content:[
        '- Developed a backtracking sudoku application with the solving process shown through\
        the visualizer',
        '- Designed with an emphasis on efficiency of code, utilizing effective commenting\
        and organization of functions to yield an easily readable and understandable final product',
        '- Implemented handling of unsolvable grids',
      ],
    },
  ];

  return (
    <div className='projects_items'>

      {projects.map(i => (
        <div className='projects_items_card'>

          <a href={i.link} target='_blank' rel='noopener noreferrer' className='projects_items_card_link fs1'
            style={{fontWeight: 'bold'}}>{i.name}</a>
          <div className='projects_items_card_description fs1'>{i.description}</div>
          <div className='projects_items_card_technologies fs1'>{i.technologies}</div>
          {i.content.map(j => (
            <ul>
              <li className='projects_items_card_maintext fs1'>{j}</li>
            </ul>
          ))}



        </div>
      ))}


    </div>
  );
}


function Projects() {
  return (
    <div className='projects_main' id='projects'>

      <div className='projects'>

        <SectionHeading text='Technical Projects' color='white' />
        <ShowProjects />

      </div>

    </div>
  )
}


export default Projects;
