import './TeachingInvolvements.sass';
import SectionHeading from '../../Components/SectionHeading/SectionHeading.js';
import Mathnasium from '../../Components/Images/Mathnasium.png';
import Stemexcel from '../../Components/Images/Stemexcel.png';


function ShowTi() {
  const ti = [
    {
      place:'Mathnasium',
      title:'Mathematics Tutor',
      link:'https://www.mathnasium.com/fallschurch',
      date:'Oct 2020 - Jun 2021',
      content:[
        '- Tutored 30+ elementary, middle, and high school students core mathematics course\
          topics [through calc I], in addition to SAT/ACT prep',
        '- Targeted personalized key areas of improvement, emphasizing experimentation of\
          current topics with previously known material',
        '- Helped students reach a higher level of understanding in coursework and advance\
          topics, resulting in higher quiz and test scores post-instruction'
      ],
      image: Mathnasium,
    },
    {
      place:'STEM exCEL',
      title:'Python Instructor',
      link:'https://stemexcel.org',
      date:'Nov 2019 - Mar 2020',
      content:[
        '- Instructed 20+ 4th - 6th grade students Python programming, introducing data\
          types, lists, loops, control flow, random, and other topics',
        '- Introduced students to text-based terminal projects, such as random password\
          and unique username generators',
      ],
      image: Stemexcel,
    },
  ];

  return (
    <div className='ti_items'>

      {ti.map(i => (
        <div className='ti_items_card'>

          <img src={i.image} className='ti_items_card_image' /><br/>
          <a href={i.link} className='ti_items_card_link fs1' target='_blank' rel='noopener noreferrer' style={{fontWeight: 'bold'}}>{i.place}</a>
          <div className='ti_items_card_title fs1'>{i.title}</div>
          <div className='ti_items_card_date fs1'>{i.date}</div>
          {i.content.map(j => (
            <ul>
            <li className='ti_items_card_maintext fs1'>{j}</li>
            </ul>
          ))}

        </div>
      ))}


    </div>
  );
}



function Ti() {
  return (
    <div className='ti_main' id='teaching'>

      <div className='ti'>

        <SectionHeading text='Teaching Involvements' color='white' />
        <ShowTi />

      </div>

    </div>
  )
}


export default Ti;
